import React, {useEffect,useState} from 'react';

const BasicsInput = props => {
    
 
    const [enteredName, setEnteredName] = useState('');
    const [isValidName, setIsValidName] = useState(true);
    const [formIsValid, setFormIsValid] = useState(false);
    const [enteredEmail, setEnteredEmail] = useState('');

    const emailChangeHandler = (event).

    useEffect(() => {
        if(isValidName){
            setFormIsValid(true)
        }else{
            setFormIsValid(false)
        };
    },[isValidName]); 
    const nameChangeHandler = event => {
        setEnteredName(event.target.value);
        if(event.target.value.trim() !== ''){
            setIsValidName(true);  
        };   
    };
    const onBlurChangeHandler = () => {
        if(enteredName.trim() === ''){
            setIsValidName(false);
            return;
        }
    };
    const formSubmitHandler = event => {
        event.preventDefault();
        if(enteredName.trim() === ''){
            setIsValidName(false)
            return;
        };
        console.log(enteredName);
        setEnteredName('');
        setIsValidName(true);
    };
    const classes = isValidName ? 'form-control':'form-control invalid';
    return(
        <form onSubmit={formSubmitHandler}>
            <div className={classes}>
                <label>Your Name</label>
                <input onBlur={onBlurChangeHandler} value={enteredName} type='text' id='name' onChange={nameChangeHandler}/>
                {!isValidName && <p style={{color:'red'}}>Should be enter something</p>}
                <label>Your Email</label>
                <input />
            </div>
           
            <div className="form-actions">
                <button disabled={!formIsValid}>Submit</button>
            </div>
        </form>
    );
}

export default BasicsInput;
