import React, {useState} from 'react';

const BasicsInput = props => {
    
 
    const [enteredName, setEnteredName] = useState('');
    const [isValidName, setIsValidName] = useState(true);
    const [enteredEmail, setEnteredEmail] = useState('');
    const [isValidEmail, setIsValidEmail] = useState(true);
    
    
    
    let formIsValid = false;
    if(isValidName){
        formIsValid =true;
    } 
    const nameChangeHandler = event => {
        setEnteredName(event.target.value); 
    };
    const emailChangeHandler = (event) => {
        setEnteredEmail(event.target.value);
    }
    const nameInputBlurHandler = () => {
        setIsValidName(false);
        return;
    }
    
    const formSubmitHandler = event => {
        event.preventDefault();
        if(enteredName.trim() === ''){
            setIsValidName(false);
            return;
        };
        console.log(enteredName);
        setEnteredName('');
        setIsValidName(true)
    };
    const checkName = isValidName 
    ? 'form-control'
    :'form-control invalid';
    return(
        <form onSubmit={formSubmitHandler}>
            <div className={checkName}>
                <label>Your Name</label>
                <input onBlur={nameInputBlurHandler} value={enteredName} type='text' id='name' onChange={nameChangeHandler}/>
                {!isValidName && <p style={{color:'red'}}>Should be enter something</p>}
            </div>
            <div className={checkName}>
                <label>E-mail</label>
                <input onBlur={nameInputBlurHandler} value={enteredEmail} type='email' id='name' onChange={emailChangeHandler}/>
                {!isValidEmail && <p style={{color:'red'}}>Should be enter something</p>}
            </div>
           
            <div className="form-actions">
                <button disabled={!formIsValid} >Submit</button>
            </div>
        </form>
    );
};
export default BasicsInput;
